DROP TABLE IF EXISTS `notification_trigger_status`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `notification_trigger_status`  AS SELECT `nt`.`id` AS `id`, `nt`.`name` AS `name`, `nt`.`trigger_type` AS `trigger_type`, `nt`.`status` AS `status`, `nt`.`next_run` AS `next_run`, `nt`.`last_run` AS `last_run`, `nt`.`run_count` AS `run_count`, CASE WHEN `nt`.`next_run` <= CURRENT_TIMESTAMP AND `nt`.`status` = 'active' THEN 'due' WHEN `nt`.`status` = 'active' THEN 'scheduled' ELSE `nt`.`status` END AS `current_status`, count(`ntl`.`id`) AS `total_executions`, sum(case when `ntl`.`execution_status` = 'completed' then 1 else 0 end) AS `successful_executions`, sum(case when `ntl`.`execution_status` = 'failed' then 1 else 0 end) AS `failed_executions`, max(`ntl`.`executed_at`) AS `last_execution` FROM (`notification_triggers` `nt` left join `notification_trigger_log` `ntl` on(`nt`.`id` = `ntl`.`trigger_id`)) GROUP BY `nt`.`id`, `nt`.`name`, `nt`.`trigger_type`, `nt`.`status`, `nt`.`next_run`, `nt`.`last_run`, `nt`.`run_count` ;
DROP TABLE IF EXISTS `two_factor_stats`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `two_factor_stats`  AS SELECT count(0) AS `total_admins`, sum(case when `admins`.`two_factor_enabled` = 1 then 1 else 0 end) AS `admins_with_2fa`, sum(case when `admins`.`two_factor_setup_completed` = 1 then 1 else 0 end) AS `admins_setup_completed`, sum(case when `admins`.`account_locked` = 1 then 1 else 0 end) AS `locked_accounts`, round(sum(case when `admins`.`two_factor_enabled` = 1 then 1 else 0 end) / count(0) * 100,2) AS `two_factor_adoption_rate` FROM `admins` WHERE `admins`.`status` = 'Active' ;
